def is_adn(text):
    text = text.upper()
    x = "ATCG"
    y = 0
    for i in (len(text)):
        if x not in text[i] or text == "":
            y += 1 
    if y == 1:
        return False
    else:
        return True

def positions(text, car):
    occu = []
    car_len = len(car)
    for i in range (len(text)):
        if text[i] == car[0]:
            if car == text[i:(i+car_len)]: 
                occu.append(i)
    return occu

def distance_h(adn1, adn2):
    y = 0
    if len(adn2) != len(adn2):
        return None
    else:
        for i in range (len(adn1)):
            if adn1[i] != adn2[i]:
                y += 1
        return y 

def distance_matrice(l):
    matrix = []
    for i in range (len(l)):
        matrix.append([])
        for f in range (len(l)):
            result = distance_h(l[i], l[f])
            matrix[i].append(result)
    return matrix

